mapper.select:
select * from well_data_his
where id in (select mid from (
select to_char(stime,'yyyy-mm-dd hh24'),min(id) mid from well_data_his
where well_id=#{wellId}
and well_id in (select id from well_info where AUTOMATION_MARK=0 and mark=0)

  AND to_char(stime,'yyyy-mm-dd hh24:mi:ss')>=#{timeStart}



  AND to_char(stime,'yyyy-mm-dd hh24:mi:ss')<=#{timeEnd}


group by to_char(stime,'yyyy-mm-dd hh24') order by to_char(stime,'yyyy-mm-dd hh24')

)
)
ORDER BY stime desc

select * from well_data_his
where id in (select mid from (
select to_char(stime,'yyyy-mm-dd'),min(id) mid from well_data_his
where well_id=#{wellId}
and well_id in (select id from well_info where AUTOMATION_MARK=0 and mark=0)

  AND to_char(stime,'yyyy-mm-dd hh24:mi:ss')>=#{timeStart}



  AND to_char(stime,'yyyy-mm-dd hh24:mi:ss')<=#{timeEnd}


group by to_char(stime,'yyyy-mm-dd') order by to_char(stime,'yyyy-mm-dd')
)
)
ORDER BY stime desc

select a.*,b.FLOW_GAS_DAY f_gas_day,b.FLOW_WATER_DAY f_water_day from well_data_his a
LEFT JOIN well_report_day b on to_char(a.stime,'yyyy-mm-dd')=b.REPORT_DATE and a.well_id=b.well_id
where a.id in (select mid from (
select to_char(stime,'yyyy-mm-dd'),min(id) mid from well_data_his
where well_id=#{wellId}
and well_id in (select id from well_info where AUTOMATION_MARK=0 and mark=0)

  AND to_char(stime,'yyyy-mm-dd hh24:mi:ss')>=#{timeStart}



  AND to_char(stime,'yyyy-mm-dd hh24:mi:ss')<=#{timeEnd}


group by to_char(stime,'yyyy-mm-dd') order by to_char(stime,'yyyy-mm-dd')

)
)
ORDER BY a.stime

select * from well_data_his
